import React, {useState} from 'react'
import './home.css'
import Fitness from '../../assets/Fitness.png'
import CalorieCalculator from '../../components/calories/CalorieCalculator';
const Home = () => {

  
    const [showCalculator, setShowCalculator] = useState(false);
  
    const handleShowCalculator = () => {
      setShowCalculator(true);
    

  return (
    
    <div className="container">
      <div className="left-section">
        <h1 className="headline">Your Fitness Journey Starts Here</h1>
        {showCalculator ? (
          <CalorieCalculator /> ) : (
          <button onClick={handleShowCalculator}>Track Your Weight</button>
          )}
       
      </div>
      <div className="right-section">
        <img src={Fitness} alt="Fitness" className="fitness-model-img" />
      </div>
    </div>
  
  );
};
};


export default Home;
