import React, { useState } from 'react';
import './services.css'
const CalorieCalculator = () => {
  const [weight, setWeight] = useState('');
  const [height, setHeight] = useState('');
  const [age, setAge] = useState('');
  const [gender, setGender] = useState('male');
  const [activityLevel, setActivityLevel] = useState('sedentary');
  const [calorieExpenditure, setCalorieExpenditure] = useState(0);

  const calculateCalorieExpenditure = () => {
    // Calculate calorie expenditure based on the input values
    let bmr = 0;
    if (gender === 'male') {
      bmr = 10 * weight + 6.25 * height - 5 * age + 5;
    } else {
      bmr = 10 * weight + 6.25 * height - 5 * age - 161;
    }

    let calorieMultiplier = 1;
    if (activityLevel === 'sedentary') {
      calorieMultiplier = 1.2;
    } else if (activityLevel === 'lightlyActive') {
      calorieMultiplier = 1.375;
    } else if (activityLevel === 'moderatelyActive') {
      calorieMultiplier = 1.55;
    } else if (activityLevel === 'veryActive') {
      calorieMultiplier = 1.725;
    } else if (activityLevel === 'extraActive') {
      calorieMultiplier = 1.9;
    }

    const calorieExpenditure = Math.round(bmr * calorieMultiplier);
    setCalorieExpenditure(calorieExpenditure);
  };

  return (
    <div className="calorie-calculator">
      <h2 className="title">Calculate Your Daily Calorie Expenditure</h2>
      <div className="form">
        <label>
          Weight (in kg):
          <input type="number" value={weight} onChange={(e) => setWeight(e.target.value)} />
        </label>
        <label>
          Height (in cm):
          <input type="number" value={height} onChange={(e) => setHeight(e.target.value)} />
        </label>
        <label>
          Age (in years):
          <input type="number" value={age} onChange={(e) => setAge(e.target.value)} />
        </label>
        <label>
          Gender:
          <select value={gender} onChange={(e) => setGender(e.target.value)}>
            <option value="male">Male</option>
            <option value="female">Female</option>
          </select>
        </label>
        <label>
          Activity Level:
          <select value={activityLevel} onChange={(e) => setActivityLevel(e.target.value)}>
            <option value="sedentary">Sedentary (little to no exercise)</option>
            <option value="lightlyActive">Lightly Active (light exercise/sports 1-3 days/week)</option>
            <option value="moderatelyActive">Moderately Active (moderate exercise/sports 3-5 days/week)</option>
            <option value="veryActive">Very Active (hard exercise/sports 6-7 days/week)</option>
            <option value="extraActive">Extra Active (very hard exercise/sports & physical job or 2x training)</option>
          </select>
        </label>
        <button className="calculate-button" onClick={calculateCalorieExpenditure}>Calculate</button>
      </div>
      </div>

);
}
export default CalorieCalculator;
